
public class 문자열_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="Hello ";
		String s2="Java";
		System.out.println(s1+s2);//문자열 결합 => 사용 빈도 더 많음
		System.out.println(s1.concat(s2));//문자열 결합 같은 결과 다른 방법
		// 오라클 => 문자열 ||, concat(s1,s2)

	}

}
